mapper.select:
SELECT tran_no , prod_no , buyer_id, payment_option , receiver_name, receiver_phone, demailaddr, dlvy_request, TRIM(tran_status_code) tran_status_code, order_date, dlvy_date
FROM transaction 
WHERE tran_no = #{value}

SELECT tran_no, TRIM(tran_status_code) tran_status_code
FROM transaction 
WHERE prod_no = #{value}

mapper.update:
UPDATE transaction
payment_option = #{paymentOption} , receiver_name = #{receiverName} , receiver_phone = #{receiverPhone} , demailAddr = #{divyAddr} , dlvy_request = #{divyRequest} , dlvy_date = #{divyDate} ,
WHERE tran_no= #{tranNo}

UPDATE transaction 
SET tran_status_code = #{tranCode}
WHERE tran_no = #{tranNo}
