mapper.update:
UPDATE product
prod_name = #{prodName} , prod_detail = #{prodDetail} , manufacture_day = #{manuDate} , price = #{price} , image_file = #{fileName} ,
WHERE prod_no = #{prodNo}

mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY
price DESC price pd.prod_no
) rn, pd.prod_no, pd.prod_name, pd.price, pd.reg_date, NVL(ts.tran_status_code,0) tran_code, COUNT(*)OVER() count FROM product pd, transaction ts
pd.prod_no=ts.prod_no(+) AND prod_no= #{searchKeyword} AND prod_name LIKE '%#{searchKeyword}' AND ts.tran_status_code IS NULL AND ts.tran_status_code = 1 )
WHERE rn BETWEEN #{startRowNum} AND #{endRowNum}
